mapper.update:
update usercart set
cquantity=#{param2}
where ucId=#{param1}

update userorder set
statuscode = #{param2}
where oId = #{param1}

mapper.select:
select * from(
select rownum rn, oId, mId, pId, quantity, odate, price, statusCode, pname, statusName from ( select a.oId, a.mId, a.pId, a.quantity, a.odate, a.price, a.statusCode, b.pName, c.statusName from userOrder a, product b, statusType c where a.pid = b.pid and a.statusCode = c.statusCode
order by oId desc )) where rn between #{pageStart} and #{pageEnd}
